  function scrollToSlide1(index: number) {
    if (!carouselContainer1) return;
    currentSlide1 = index;
    carouselContainer1.scrollTo({
      left: carouselContainer1.clientWidth * index,
      behavior: "smooth",
    });
  }

  function scrollToSlide2(index: number) {
